SELECT DISTINCT
EXTRACTVALUE(VALUE(TT1),'KLADRCode') KLADRCode,
EXTRACTVALUE(VALUE(TT2),'CatCar') CatCar
FROM RSA_EGARANT.INS_REQUEST_JOUR irj
